script:
if doc.purpose == "Manufacture":
    stock_entry = frappe.new_doc("Stock Entry")
    stock_entry.stock_entry_type = "Handling Loss"
    stock_entry_flag = False
    for itm in doc.items:
        if itm.custom_handling_loss_qty > 0 and itm.s_warehouse:
            stock_entry_flag = True
            stock_entry.append('items', {
                'item_code': itm.item_code, 
                's_warehouse': itm.s_warehouse,
                'qty': itm.custom_handling_loss_qty
            })
    if stock_entry_flag:
        stock_entry.insert()
        stock_entry.submit()
