script:
if doc.purpose == "Manufacture" and doc.work_order and not doc.custom_bypass_validation:
    work_order = frappe.get_doc("Work Order", doc.work_order)
    expected_items_count = len(work_order.required_items) + 1

    if len(doc.items) != expected_items_count:
        frappe.throw(("The number of items in the Work Order ({}) does not match the Stock Entry items ({}).").format(len(work_order.required_items), len(doc.items)))

    finished_item = doc.get('items', {'is_finished_item': 1})
    in_qty = finished_item[0].qty if finished_item else frappe.throw("Finished Item Qty can't be zero")

    for item in doc.items:
        if not item.is_finished_item:
            required_item = next((ri for ri in work_order.required_items if ri.item_code == item.item_code), None)
            if required_item:
                scaled_qty = (required_item.required_qty / work_order.qty) * in_qty
                if not frappe.utils.flt(item.qty, 6) == frappe.utils.flt(scaled_qty, 6):
                    frappe.throw(("The raw material item {} (Stock Entry qty: {}) does not match the calculated qty ({}).")
                                 .format(item.item_code, item.qty, scaled_qty))

if doc.items[0].sales_order and doc.is_return == 0:
    sales_inv = frappe.db.get_value("Sales Order", doc.items[0].sales_order, 'custom_sales_invoice')
    if sales_inv:
        frappe.throw(f"The Sales Invoice Is Already Submitted Against {doc.items[0].sales_order} This Sales Order")
    else:
        frappe.db.set_value("Sales Order", doc.items[0].sales_order, 'custom_sales_invoice', doc.name)

if frappe.db.exists("Price List", {'name': doc.price_list, 'custom_is_standard': 1}):
    price_list_setting = frappe.db.exists("Price List Settings", {'item_code': doc.item_code, 'standard_price_list': doc.price_list})
    if price_list_setting:
        price_list_setting = frappe.get_doc("Price List Settings", price_list_setting)
        for price_lst in price_list_setting.get('price_list_settings_changes', {'change': ['!=', 0]}):
            item_price_rate = frappe.db.get_value("Item Price", {'item_code': doc.item_code, 'price_list': price_lst.price_list}, 'price_list_rate')
            if item_price_rate is not None:
                frappe.db.set_value("Item Price", {'item_code': doc.item_code, 'price_list': price_lst.price_list}, 'price_list_rate', item_price_rate + price_lst.change)
else:
    frappe.throw("You can only change item rates for non-standard price lists.")

if doc.purpose == "Manufacture":
    stock_entry = frappe.new_doc("Stock Entry")
    stock_entry.stock_entry_type = "Handling Loss"
    stock_entry_flag = False
    for itm in doc.get('items', filters={'is_finished_item':1}):
        finished_itm = itm.item_code
        break
    for itm in doc.items:
        if itm.custom_handling_loss_qty > 0 and itm.s_warehouse:
            stock_entry_flag = True
            stock_entry.append('items', {
                'item_code': itm.item_code, 
                's_warehouse': itm.s_warehouse,
                'qty': itm.custom_handling_loss_qty,
                'custom_finished_item': finished_itm
            })
    if stock_entry_flag:
        stock_entry.insert()
        stock_entry.submit()
